import React, { Component } from 'react';
import './App.scss';

class App extends Component {
  constructor(props){
    super(props)
    this.state={
      value:''
    }
    this.handleInputChange = this.handleInputChange.bind(this)
  }

  handleInputChange = (e)=>{
    this.setState({
      value : e.target.value
    })
  }

  render() {
    return (
      <div className='text-center'>
        <input style={{width:100}} type='number' onChange={this.handleInputChange} value={this.state.value}
         pattern="[0-9]" min='000' max='999' maxlength='3'
        ></input>
        <button>Search</button>
        <div>
          <p>{this.state.value}</p>
        </div>
      </div>
    );
  }
}

export default App;
